"use strict";var isRoot=require("is-root");var createError=require("./createError");var cli=require("./cli");var renderer;function rootCheck(b,a){var c;if(b.allowRoot||a.allowRoot){return}c="Since bower is a user command, there is no need to execute it with superuser permissions.\nIf you're having permission errors when using bower without sudo, please spend a few minutes learning more about how your system should work and make any necessary repairs.\n\nhttp://www.joyent.com/blog/installing-node-and-npm\nhttps://gist.github.com/isaacs/579814\n\nYou can however run a command with sudo using --allow-root option";if(isRoot()){renderer=cli.getRenderer("",false,a);renderer.error(createError("Cannot be run with sudo","ESUDO",{details:c}));process.exit(1)}}module.exports=rootCheck;