var should=require("should");var extend=require("../index");describe("deep-extend",function(){it("can extend on 1 level",function(){var d={hello:1};var c={world:2};extend(d,c);d.should.eql({hello:1,world:2})});it("can extend on 2 levels",function(){var d={person:{name:"John"}};var c={person:{age:30}};extend(d,c);d.should.eql({person:{name:"John",age:30}})});it("can extend with Buffer values",function(){var d={hello:1};var c={value:new Buffer("world")};extend(d,c);d.should.eql({hello:1,value:new Buffer("world")})});it("Buffer is cloned",function(){var d={};var c={value:new Buffer("foo")};extend(d,c);d.value.write("bar");d.value.toString().should.eql("bar");c.value.toString().should.eql("foo")});it("Date objects",function(){var d={d:new Date()};var c=extend({},d);c.d.should.instanceOf(Date)});it("Date object is cloned",function(){var d={d:new Date()};var c=extend({},d);c.d.setTime((new Date()).getTime()+100000);c.d.getTime().should.not.eql(d.d.getTime())})});