var path=require("path"),util=require("util"),fs=require("fs"),utl=require("./utl"),home=process.env.HOME,settings;function getSettings(b){if(settings){return settings}try{settingsJson=fs.readFileSync(path.join(b||home,".cardinalrc"),"utf-8")}catch(a){return undefined}try{return JSON.parse(settingsJson)}catch(c){console.error(c);return undefined}}function resolveTheme(c){var a,b=getSettings(c);if(!b||!b.theme){return undefined}try{a=utl.isPath(b.theme)?b.theme:path.join(__dirname,"themes",b.theme);return require(a)}catch(d){console.error(d);return undefined}}module.exports={resolveTheme:resolveTheme,getSettings:getSettings};