"use strict";var test=require("tap").test,util=require("util"),customTheme=require("./fixtures/custom"),cardinal=require("..");function inspect(a){return console.log(util.inspect(a,false,5,false))}var json=JSON.stringify({foo:"bar",baz:"quux",bam:null});test("supplying custom theme",function(a){var b=cardinal.highlight(json,{json:true,theme:customTheme});a.equals(b,'\u001b[33m{\u001b[39m\u001b[92m"foo"\u001b[39m\u001b[93m:\u001b[39m\u001b[92m"bar"\u001b[39m\u001b[32m,\u001b[39m\u001b[92m"baz"\u001b[39m\u001b[93m:\u001b[39m\u001b[92m"quux"\u001b[39m\u001b[32m,\u001b[39m\u001b[92m"bam"\u001b[39m\u001b[93m:\u001b[39m\u001b[90mnull\u001b[39m\u001b[33m}\u001b[39m');a.end()});test("not supplying custom theme",function(a){var b=cardinal.highlight(json,{json:true});a.equals(b,'\u001b[33m{\u001b[39m\u001b[92m"foo"\u001b[39m\u001b[93m:\u001b[39m\u001b[92m"bar"\u001b[39m\u001b[32m,\u001b[39m\u001b[92m"baz"\u001b[39m\u001b[93m:\u001b[39m\u001b[92m"quux"\u001b[39m\u001b[32m,\u001b[39m\u001b[92m"bam"\u001b[39m\u001b[93m:\u001b[39m\u001b[90mnull\u001b[39m\u001b[33m}\u001b[39m');a.end()});test("without json option",function(a){try{cardinal.highlight(json)}catch(b){a.similar(b.message,/Unable to perform highlight. The code contained syntax errors.* Line 1: Unexpected token /);a.end()}});