"use strict";var test=require("tap").test,path=require("path"),fs=require("fs"),themesdir=path.join(__dirname,"..","themes"),allFiles=fs.readdirSync(themesdir);test("validate themes by requiring all of them",function(a){allFiles.filter(function(b){return path.extname(b)===".js"}).forEach(function(c){try{a.ok(require(path.join(themesdir,c)),c+" is valid")}catch(b){a.fail("theme: "+c+" is invalid! "+b.message)}});a.end()});