var mkdirp=require("mkdirp");var path=require("path");var i=0;var tap=require("tap");var fs=require("fs");var rimraf=require("rimraf");var fixtures=path.resolve(__dirname,"fixtures");var chars=["a","b","c"];var dirs=[];for(var i=0;i<3;i++){for(var j=0;j<3;j++){for(var k=0;k<3;k++){dirs.push(chars[i]+"/"+chars[j]+"/"+chars[k])}}}var files=[];for(var i=0;i<3;i++){for(var j=0;j<3;j++){for(var k=0;k<3;k++){files.push(chars[i]+chars[j]+chars[k]);files.push("."+chars[i]+chars[j]+chars[k])}}}tap.test("remove fixtures",function(a){rimraf(path.resolve(__dirname,"fixtures"),function(b){a.ifError(b,"remove fixtures");a.end()})});tap.test("create fixtures",function(a){dirs.forEach(function(b){b=path.resolve(fixtures,b);a.test("mkdir "+b,function(c){mkdirp(b,function(d){c.ifError(d,"mkdir "+b);if(d){return c.end()}files.forEach(function(e){e=path.resolve(b,e);c.test("writeFile "+e,function(f){fs.writeFile(e,path.basename(e),function(g){f.ifError(g,"writing "+e);f.end()})})});c.end()})})});a.end()});