function mergeSort(a,e){if(a==null){return[]}else{if(a.length<2){return a}}if(e==null){e=defaultCompare}var b,d,c;b=~~(a.length/2);d=mergeSort(a.slice(0,b),e);c=mergeSort(a.slice(b,a.length),e);return merge(d,c,e)}function defaultCompare(d,c){return d<c?-1:(d>c?1:0)}function merge(c,b,d){var a=[];while(c.length&&b.length){if(d(c[0],b[0])<=0){a.push(c.shift())}else{a.push(b.shift())}}if(c.length){a.push.apply(a,c)}if(b.length){a.push.apply(a,b)}return a}module.exports=mergeSort;