"use strict";var Q=require("../q");suite("Chaining",function(){var a=1000;bench("Chaining many already-fulfilled promises together",function(b){var d=Q();for(var c=0;c<a;++c){d=d.then(function(){return Q()})}d.then(b)});bench("Chaining and then fulfilling the end of the chain",function(b){var c=Q.defer();var e=c.promise;for(var d=0;d<a;++d){(function(){var f=e;e=Q().then(function(){return f})}())}e.then(b);c.resolve()})});