var test=require("tape");var parse=require("../").parse;test("parse shell commands",function(a){a.same(parse("a 'b' \"c\""),["a","b","c"]);a.same(parse('beep "boop" \'foo bar baz\' "it\'s \\"so\\" groovy"'),["beep","boop","foo bar baz",'it\'s "so" groovy']);a.same(parse("a b\\ c d"),["a","b c","d"]);a.same(parse("\\$beep bo\\`op"),["$beep","bo`op"]);a.same(parse('echo "foo = \\"foo\\""'),["echo",'foo = "foo"']);a.same(parse(""),[]);a.same(parse(" "),[]);a.same(parse("\t"),[]);a.same(parse('a"b c d"e'),["ab c de"]);a.same(parse('a\\ b"c d"\\ e f'),["a bc d e","f"]);a.same(parse("a\\ b\"c d\"\\ e'f g' h"),["a bc d ef g","h"]);a.same(parse("x \"bl'a\"'h'"),["x","bl'ah"]);a.end()});