var test=require("tape");var quote=require("../").quote;test("quote",function(a){a.equal(quote(["a","b","c d"]),"a b 'c d'");a.equal(quote(["a","b",'it\'s a "neat thing"']),'a b "it\'s a \\"neat thing\\""');a.equal(quote(["$","`","'"]),'\\$ \\` "\'"');a.equal(quote([]),"");a.equal(quote(["a\nb"]),"'a\nb'");a.equal(quote([" #(){}*|][!"]),"' #(){}*|][!'");a.equal(quote(["'#(){}*|][!"]),'"\'#(){}*|][\\!"');a.equal(quote(["X#(){}*|][!"]),"X\\#\\(\\){}\\*\\|][\\!");a.equal(quote(["a\n#\nb"]),"'a\n#\nb'");a.equal(quote(["a",1,true,false]),"a 1 true false");a.equal(quote(["a",1,null,undefined]),"a 1 null undefined");a.end()});test("quote ops",function(a){a.equal(quote(["a",{op:"|"},"b"]),"a \\| b");a.equal(quote(["a",{op:"&&"},"b",{op:";"},"c"]),"a \\&\\& b \\; c");a.end()});