var test=require("tape");var parse=require("../").parse;test("set env vars",function(a){a.same(parse("ABC=444 x y z"),["ABC=444","x","y","z"]);a.same(parse("ABC=3\\ 4\\ 5 x y z"),["ABC=3 4 5","x","y","z"]);a.same(parse('X="7 8 9" printx'),["X=7 8 9","printx"]);a.same(parse('X="7 8 9"; printx'),["X=7 8 9",{op:";"},"printx"]);a.same(parse('X="7 8 9"; printx',function(b){a.fail("should not have matched any keys")}),["X=7 8 9",{op:";"},"printx"]);a.end()});