var path=require("path"),expect=require("expect.js"),fs=require("fs"),helpers=require("../helpers"),bower=helpers.require("lib/index");describe("bower init",function(){var e=new helpers.TempDir,t=path.join(e.path,"bower.json"),r={cwd:e.path,interactive:!0};it("generates bower.json file",function(){e.prepare();var n=bower.commands.init(r);return helpers.expectEvent(n,"prompt").spread(function(e,t){return t({name:"test-name",version:"test-version",description:"test-description",moduleType:"test-moduleType",keywords:"test-keyword",authors:"test-author",license:"test-license",homepage:"test-homepage","private":!0}),helpers.expectEvent(n,"prompt")}).spread(function(e,t){return t({prompt:!0}),helpers.expectEvent(n,"end")}).then(function(){expect(fs.existsSync(t)).to.be(!0)})})});
