var path=require("path");var expect=require("expect.js");var fs=require("fs");var helpers=require("../helpers");var bower=helpers.require("lib/index");describe("bower uninstall",function(){var d=new helpers.TempDir({"bower.json":{name:"hello-world",dependencies:{underscore:"*"}}});beforeEach(function(){d.prepare()});var c=path.join(d.path,"bower.json");function a(){return JSON.parse(fs.readFileSync(c))}var b={cwd:d.path,interactive:true};it("does not remove anything from dependencies by default",function(){var e=bower.commands.uninstall(["underscore"],undefined,b);return helpers.expectEvent(e,"end").then(function(){expect(a().dependencies).to.eql({underscore:"*"})})});it("removes dependency from bower.json if --save flag is used",function(){var e=bower.commands.uninstall(["underscore"],{save:true},b);return helpers.expectEvent(e,"end").then(function(){expect(a().dependencies).to.eql({})})})});