var Code=require("code");var Lab=require("lab");var Qs=require("../");var internals={};var lab=exports.lab=Lab.script();var expect=Code.expect;var describe=lab.experiment;var it=lab.test;describe("stringify()",function(){it("stringifies a querystring object",function(a){expect(Qs.stringify({a:"b"})).to.equal("a=b");expect(Qs.stringify({a:1})).to.equal("a=1");expect(Qs.stringify({a:1,b:2})).to.equal("a=1&b=2");a()});it("stringifies a nested object",function(a){expect(Qs.stringify({a:{b:"c"}})).to.equal("a%5Bb%5D=c");expect(Qs.stringify({a:{b:{c:{d:"e"}}}})).to.equal("a%5Bb%5D%5Bc%5D%5Bd%5D=e");a()});it("stringifies an array value",function(a){expect(Qs.stringify({a:["b","c","d"]})).to.equal("a%5B0%5D=b&a%5B1%5D=c&a%5B2%5D=d");a()});it("omits array indices when asked",function(a){expect(Qs.stringify({a:["b","c","d"]},{indices:false})).to.equal("a=b&a=c&a=d");a()});it("stringifies a nested array value",function(a){expect(Qs.stringify({a:{b:["c","d"]}})).to.equal("a%5Bb%5D%5B0%5D=c&a%5Bb%5D%5B1%5D=d");a()});it("stringifies an object inside an array",function(a){expect(Qs.stringify({a:[{b:"c"}]})).to.equal("a%5B0%5D%5Bb%5D=c");expect(Qs.stringify({a:[{b:{c:[1]}}]})).to.equal("a%5B0%5D%5Bb%5D%5Bc%5D%5B0%5D=1");a()});it("does not omit object keys when indices = false",function(a){expect(Qs.stringify({a:[{b:"c"}]},{indices:false})).to.equal("a%5Bb%5D=c");a()});it("stringifies a complicated object",function(a){expect(Qs.stringify({a:{b:"c",d:"e"}})).to.equal("a%5Bb%5D=c&a%5Bd%5D=e");a()});it("stringifies an empty value",function(a){expect(Qs.stringify({a:""})).to.equal("a=");expect(Qs.stringify({a:"",b:""})).to.equal("a=&b=");expect(Qs.stringify({a:null})).to.equal("a=");expect(Qs.stringify({a:{b:null}})).to.equal("a%5Bb%5D=");a()});it("stringifies an empty object",function(a){var b=Object.create(null);b.a="b";expect(Qs.stringify(b)).to.equal("a=b");a()});it("returns an empty string for invalid input",function(a){expect(Qs.stringify(undefined)).to.equal("");expect(Qs.stringify(false)).to.equal("");expect(Qs.stringify(null)).to.equal("");expect(Qs.stringify("")).to.equal("");a()});it("stringifies an object with an empty object as a child",function(a){var b={a:Object.create(null)};b.a.b="c";expect(Qs.stringify(b)).to.equal("a%5Bb%5D=c");a()});it("drops keys with a value of undefined",function(a){expect(Qs.stringify({a:undefined})).to.equal("");expect(Qs.stringify({a:{b:undefined,c:null}})).to.equal("a%5Bc%5D=");a()});it("url encodes values",function(a){expect(Qs.stringify({a:"b c"})).to.equal("a=b%20c");a()});it("stringifies a date",function(a){var b=new Date();var c="a="+encodeURIComponent(b.toISOString());expect(Qs.stringify({a:b})).to.equal(c);a()});it("stringifies the weird object from qs",function(a){expect(Qs.stringify({"my weird field":"q1!2\"'w$5&7/z8)?"})).to.equal("my%20weird%20field=q1!2%22'w%245%267%2Fz8)%3F");a()});it("skips properties that are part of the object prototype",function(a){Object.prototype.crash="test";expect(Qs.stringify({a:"b"})).to.equal("a=b");expect(Qs.stringify({a:{b:"c"}})).to.equal("a%5Bb%5D=c");delete Object.prototype.crash;a()});it("stringifies boolean values",function(a){expect(Qs.stringify({a:true})).to.equal("a=true");expect(Qs.stringify({a:{b:true}})).to.equal("a%5Bb%5D=true");expect(Qs.stringify({b:false})).to.equal("b=false");expect(Qs.stringify({b:{c:false}})).to.equal("b%5Bc%5D=false");a()});it("stringifies buffer values",function(a){expect(Qs.stringify({a:new Buffer("test")})).to.equal("a=test");expect(Qs.stringify({a:{b:new Buffer("test")}})).to.equal("a%5Bb%5D=test");a()});it("stringifies an object using an alternative delimiter",function(a){expect(Qs.stringify({a:"b",c:"d"},{delimiter:";"})).to.equal("a=b;c=d");a()});it("doesn't blow up when Buffer global is missing",function(a){var b=global.Buffer;delete global.Buffer;expect(Qs.stringify({a:"b",c:"d"})).to.equal("a=b&c=d");global.Buffer=b;a()})});