var assert=require("assert"),request=require("request"),vows=require("vows"),union=require("../");vows.describe("union/status-code").addBatch({"When using `union`":{"with a server setting `res.statusCode`":{topic:function(){var e=union.createServer({before:[function(e,s){s.statusCode=404,s.end()}]});e.listen(9091,this.callback)},"and sending a request":{topic:function(){request("http://localhost:9091/",this.callback)},"it should have proper `statusCode` set":function(e,s){assert.isTrue(!e),assert.equal(s.statusCode,404)}}}}})["export"](module);