var runWithCover=require("./common/run-with-cover"),util=require("util"),Command=require("./index");function TestCommand(){Command.call(this)}TestCommand.TYPE="test";util.inherits(TestCommand,Command);Command.mix(TestCommand,{synopsis:function(){return"cover a node command only when npm_config_coverage is set. Use in an `npm test` script for conditional coverage"},usage:function(){runWithCover.usage(this.toolName(),this.type())},run:function(a,b){runWithCover.run(a,this.type(),!!process.env.npm_config_coverage,b)}});module.exports=TestCommand;