var util=require("util"),Store=require("./index");function MemoryStore(){Store.call(this);this.map={}}MemoryStore.TYPE="memory";util.inherits(MemoryStore,Store);Store.mix(MemoryStore,{set:function(a,b){this.map[a]=b},get:function(a){if(!this.hasKey(a)){throw new Error("Unable to find entry for ["+a+"]")}return this.map[a]},hasKey:function(a){return this.map.hasOwnProperty(a)},keys:function(){return Object.keys(this.map)},dispose:function(){this.map={}}});module.exports=MemoryStore;