var OPT_PREFIX="      ",OPT_START=OPT_PREFIX.length,TEXT_START=14,STOP=80,wrap=require("wordwrap")(TEXT_START,STOP),paraWrap=require("wordwrap")(1,STOP);function formatPara(a){return paraWrap(a)}function formatOption(c,a){var b=wrap(a);if(c.length>TEXT_START-OPT_START-2){return OPT_PREFIX+c+"\n"+b}else{return OPT_PREFIX+c+b.substring((OPT_PREFIX+c).length)}}module.exports={formatPara:formatPara,formatOption:formatOption};