function TAP(o){Base.call(this,o);var n=(this.stats,1),e=0,s=0;o.on("start",function(){var n=o.grepTotal(o.suite);console.log("%d..%d",1,n)}),o.on("test end",function(){++n}),o.on("pending",function(o){console.log("ok %d %s # SKIP -",n,title(o))}),o.on("pass",function(o){e++,console.log("ok %d %s",n,title(o))}),o.on("fail",function(o,e){s++,console.log("not ok %d %s",n,title(o)),e.stack&&console.log(e.stack.replace(/^/gm,"  "))}),o.on("end",function(){console.log("# tests "+(e+s)),console.log("# pass "+e),console.log("# fail "+s)})}function title(o){return o.fullTitle().replace(/#/g,"")}var Base=require("./base"),cursor=Base.cursor,color=Base.color;exports=module.exports=TAP;