var fs=require("fs");module.exports=getOptions;function getOptions(){var c=process.argv.indexOf("--opts")!==-1?process.argv[process.argv.indexOf("--opts")+1]:"test/mocha.opts";try{var b=fs.readFileSync(c,"utf8").trim().split(/\s+/).filter(function(d){return d?true:false});process.argv=process.argv.slice(0,2).concat(b.concat(process.argv.slice(2)))}catch(a){}};