var Base=require("./base"),color=Base.color;exports=module.exports=List;function List(d){Base.call(this,d);var a=this,b=this.stats,c=d.total;d.on("start",function(){console.log(JSON.stringify(["start",{total:c}]))});d.on("pass",function(e){console.log(JSON.stringify(["pass",clean(e)]))});d.on("fail",function(f,e){f=clean(f);f.err=e.message;console.log(JSON.stringify(["fail",f]))});d.on("end",function(){process.stdout.write(JSON.stringify(["end",a.stats]))})}function clean(a){return{title:a.title,fullTitle:a.fullTitle(),duration:a.duration}};