var Base=require("./base"),cursor=Base.cursor,color=Base.color;exports=module.exports=TAP;function TAP(e){Base.call(this,e);var a=this,b=this.stats,f=1,d=0,c=0;e.on("start",function(){var g=e.grepTotal(e.suite);console.log("%d..%d",1,g)});e.on("test end",function(){++f});e.on("pending",function(g){console.log("ok %d %s # SKIP -",f,title(g))});e.on("pass",function(g){d++;console.log("ok %d %s",f,title(g))});e.on("fail",function(h,g){c++;console.log("not ok %d %s",f,title(h));if(g.stack){console.log(g.stack.replace(/^/gm,"  "))}});e.on("end",function(){console.log("# tests "+(d+c));console.log("# pass "+d);console.log("# fail "+c)})}function title(a){return a.fullTitle().replace(/#/g,"")};