var fs=require("fs");var path=require("path");var which=require("which");try{exports.path=path.resolve(__dirname,require("./location").location)}catch(e){exports.path=null}exports.version="1.9.8";exports.cleanPath=function(a){return a.replace(/:[^:]*node_modules[^:]*/g,"").replace(/(^|:)\.\/bin(\:|$)/g,":").replace(/^:+/,"").replace(/:+$/,"")};if(exports.path){try{var st=fs.statSync(exports.path);var mode=st.mode|365;if(mode!==st.mode){fs.chmodSync(exports.path,mode)}}catch(e){}};